import { ImBookmarks } from "react-icons/im";
import SectionHead from "./SectionHead";
import { programs } from "../data";
import Card from "../UI/Card";
import { Link } from "react-router-dom";
import { GiArmoredBoomerang } from "react-icons/gi";
// import Modal from "./components/Modal";
import axios from "axios";
import { useState } from "react";
import SetVarMainTemplatePlugin from "webpack/lib/SetVarMainTemplatePlugin";

const Programs = (props) => {
	const client = props.client;
	const [title, setTitle] = useState(props.title || "");
	const [detail, setDetail] = useState(props.detail || "");

	const saveCourse = function () {
		let program = {
			title: title,
			detail: detail,
		};

		client.coursesCreate({ "program": program }).then((result) => {
			props.courseSaved(result)
		})
	};

	return (
		<section className="programs">
			<div className="container programs__container">
				<SectionHead icon={<ImBookmarks />} title="Programs" />
				<div className="programs__wrapper">
					{programs.map(({ id, icon, title, info, path }) => {
						return (
							<Card className="programs__program" key={id}>
								<span>{icon}</span>
								<h4 onChange={(event)=>setTitle(event.target.value)}>{title}</h4>
								<small>{info}</small>
								<Link to={path} className="btn sm">
									Learn More
									<GiArmoredBoomerang />
								</Link>
							</Card>
						);
					})}
				</div>
			</div>
		</section>
	);
};

export default Programs;
